BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" (id SERIAL PRIMARY KEY not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" (id SERIAL PRIMARY KEY not null, "username" varchar not null, "firstname" varchar not null, "middlename" varchar, "lastname" varchar not null, "bio" text, "tagline" varchar, "avatar" varchar, "birthdate" date not null, "gender" varchar check ("gender" in ('Male', 'Female', 'Other')) not null, "phonenumber" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "status" varchar check ("status" in ('Active', 'Inactive', 'Deactivated', 'Suspended', 'Banned')) not null default 'Active', "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" (id SERIAL PRIMARY KEY not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" (id SERIAL PRIMARY KEY not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "posts" (id SERIAL PRIMARY KEY not null, "user_id" integer not null, "title" varchar not null, "slug" varchar not null, "content" text not null, "is_public" tinyint(1) not null default '1', "likes_count" integer not null default '0', "saves_count" integer not null default '0', "comments_count" integer not null default '0', "views_count" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "admins" (id SERIAL PRIMARY KEY not null, "username" varchar not null, "firstname" varchar not null, "middlename" varchar, "lastname" varchar not null, "avatar" varchar, "birthdate" date not null, "gender" varchar check ("gender" in ('Male', 'Female', 'Other')) not null, "phonenumber" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "status" varchar check ("status" in ('Active', 'Inactive', 'Deactivated', 'Suspended', 'Banned')) not null default 'Active', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "tags" (id SERIAL PRIMARY KEY not null, "admin_id" integer not null, "topic_id" integer not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("admin_id") references "admins"("id") on delete cascade, foreign key("topic_id") references "topics"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "topics" (id SERIAL PRIMARY KEY not null, "admin_id" integer not null, "name" varchar not null, "slug" varchar not null, "description" text not null, "created_at" datetime, "updated_at" datetime, foreign key("admin_id") references "admins"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "feedbacks" (id SERIAL PRIMARY KEY not null, "user_id" integer not null, "feedback" text not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "user_follows" (id SERIAL PRIMARY KEY not null, "follower_user_id" integer not null, "following_user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("follower_user_id") references "users"("id") on delete cascade, foreign key("following_user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "comments" (id SERIAL PRIMARY KEY not null, "post_id" integer not null, "user_id" integer not null, "parent_id" integer, "content" text not null, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "posts"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade, foreign key("parent_id") references "comments"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "likes" (id SERIAL PRIMARY KEY not null, "post_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "posts"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "saves" (id SERIAL PRIMARY KEY not null, "post_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "posts"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "views" (id SERIAL PRIMARY KEY not null, "post_id" integer not null, "user_id" integer, "session_id" varchar, "ip_address" varchar, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "posts"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "post_topics" (id SERIAL PRIMARY KEY not null, "topic_id" integer not null, "post_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("topic_id") references "topics"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "post_tags" (id SERIAL PRIMARY KEY not null, "tag_id" integer not null, "post_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("tag_id") references "tags"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "subscribed_emails" (id SERIAL PRIMARY KEY not null, "email" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "notifications" (id SERIAL PRIMARY KEY not null, "user_id" integer not null, "title" varchar not null, "summary" varchar not null, "details" text not null, "category" varchar not null, "link" varchar, "is_read" tinyint(1) not null default '0', "created_by" integer, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("created_by") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "users_username_unique" on "users" ("username");
CREATE UNIQUE INDEX "users_phonenumber_unique" on "users" ("phonenumber");
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "posts_slug_unique" on "posts" ("slug");
CREATE UNIQUE INDEX "admins_username_unique" on "admins" ("username");
CREATE UNIQUE INDEX "admins_phonenumber_unique" on "admins" ("phonenumber");
CREATE UNIQUE INDEX "admins_email_unique" on "admins" ("email");
CREATE UNIQUE INDEX "topics_name_unique" on "topics" ("name");
CREATE UNIQUE INDEX "topics_slug_unique" on "topics" ("slug");
CREATE UNIQUE INDEX "user_follows_unique" on "user_follows" ("follower_user_id", "following_user_id");
CREATE UNIQUE INDEX "likes_post_id_user_id_unique" on "likes" ("post_id", "user_id");
CREATE UNIQUE INDEX "saves_post_id_user_id_unique" on "saves" ("post_id", "user_id");
CREATE UNIQUE INDEX "unique_post_user_session" on "views" ("post_id", "user_id", "session_id");
CREATE UNIQUE INDEX "post_topics_post_id_topic_id_unique" on "post_topics" ("post_id", "topic_id");
CREATE UNIQUE INDEX "post_tags_post_id_tag_id_unique" on "post_tags" ("post_id", "tag_id");
CREATE UNIQUE INDEX "subscribed_emails_email_unique" on "subscribed_emails" ("email");
COMMIT;
